mapper.select:
select count(*)
from board
where board_status = 'Y' and board_type = #{i}

mapper.update:
update board
set board_count = board_count +1
where board_id = #{bId}

update board
set board_title=#{boardTitle}, board_content=#{boardContent}, board_modify_date=sysdate
where board_id=#{boardId}

update board
set board_status = 'N'
where board_id =#{bId}
